func:
let toggle = context.get("toggle") || 0;
let unit = {};

if(!toggle) /* First time */
{
    unit = {
        groups: 
        { 
            show: ["ncd-uptime:Inspect1",
                   "ncd-uptime:Inspect2",
                   "ncd-uptime:Inspect3"],
            hide: ["ncd-uptime:group1", 
                   "ncd-uptime:group2",
                   "ncd-uptime:group3",
                   "ncd-uptime:group4",
                   "ncd-uptime:group5",
                   "ncd-uptime:group6",
                   "ncd-uptime:group7",
                   "ncd-uptime:group8",
                   "ncd-uptime:group9",
                   "ncd-uptime:group10",
                   "ncd-uptime:group11",
                   "ncd-uptime:group12",
                   "ncd-uptime:Current History",
                   "ncd-uptime:Battery Level History"]
        } 
    }
    toggle++;
}
else /* Second time */
{
    unit = {
        groups: 
        { 
            hide: ["ncd-uptime:Inspect1",
                "ncd-uptime:Inspect2",
                "ncd-uptime:Inspect3"],
            show: ["ncd-uptime:group1",
                "ncd-uptime:group2",
                "ncd-uptime:group3",
                "ncd-uptime:group4",
                "ncd-uptime:group5",
                "ncd-uptime:group6",
                "ncd-uptime:group7",
                "ncd-uptime:group8",
                "ncd-uptime:group9",
                "ncd-uptime:group10",
                "ncd-uptime:group11",
                "ncd-uptime:group12",
                "ncd-uptime:Current History",
                "ncd-uptime:Battery Level History"]
        } 
    }
    toggle = 0;
}
context.set("toggle", toggle);
msg.payload = unit;
return msg;
